import React, { useEffect, useRef } from "react";
import data from "../assets/category-brands.csv";
import * as d3 from "d3";


const BarRace = ({ keyframes, duration = 250, width = 800, height = 600, n = 12 }) => {
  const svgRef = useRef();
  const barSize = 48;
  const marginTop = 16;
  const marginBottom = 6;
  const marginLeft = 0;
  const marginRight = 6;

  // Set scales
  const x = d3.scaleLinear([0, 1], [marginLeft, width - marginRight]);
  const y = d3
    .scaleBand()
    .domain(d3.range(n + 1))
    .rangeRound([marginTop, marginTop + barSize * (n + 1 + 0.1)])
    .padding(0.1);

  const colorScale = d3.scaleOrdinal(d3.schemeTableau10);

  useEffect(() => {
    const svg = d3.select(svgRef.current);
    svg
      .attr("viewBox", [0, 0, width, height])
      .attr("width", width)
      .attr("height", height);

    // Append groups for bars and labels
    const gBars = svg.append("g").attr("fill-opacity", 0.6);
    const gLabels = svg.append("g").attr("text-anchor", "end");
    const gTicker = svg.append("text").attr("x", width - 6).attr("y", marginTop).style("font", `bold ${barSize}px sans-serif`);

    const updateBars = (data, transition) => {
      const bars = gBars.selectAll("rect").data(data, (d) => d.name);

      bars
        .join(
          (enter) =>
            enter
              .append("rect")
              .attr("fill", (d) => colorScale(d.name))
              .attr("x", x(0))
              .attr("y", (d) => y(d.rank))
              .attr("width", (d) => x(d.value) - x(0))
              .attr("height", y.bandwidth()),
          (update) => update,
          (exit) =>
            exit
              .transition(transition)
              .attr("y", (d) => y(d.rank))
              .attr("width", (d) => x(d.value) - x(0))
              .remove()
        )
        .call((bar) =>
          bar
            .transition(transition)
            .attr("y", (d) => y(d.rank))
            .attr("width", (d) => x(d.value) - x(0))
        );
    };

    const updateLabels = (data, transition) => {
      const labels = gLabels.selectAll("text").data(data, (d) => d.name);

      labels
        .join(
          (enter) =>
            enter
              .append("text")
              .attr("x", -6)
              .attr("y", (d) => y(d.rank))
              .attr("dy", "0.32em")
              .attr("transform", (d) => `translate(${x(d.value)},${y(d.rank)})`)
              .text((d) => d.name),
          (update) => update,
          (exit) => exit.transition(transition).remove()
        )
        .call((label) =>
          label
            .transition(transition)
            .attr("transform", (d) => `translate(${x(d.value)},${y(d.rank)})`)
        );
    };

    const updateTicker = (date, transition) => {
      gTicker.text(date).transition(transition);
    };

    // Animate keyframes
    (async function animateChart() {
      for (const [date, data] of keyframes) {
        const transition = svg.transition().duration(duration).ease(d3.easeLinear);

        x.domain([0, d3.max(data, (d) => d.value)]);

        updateBars(data, transition);
        updateLabels(data, transition);
        updateTicker(date, transition);

        await transition.end();
      }
    })();
  }, [keyframes, duration, x, y, colorScale]);

  return <svg ref={svgRef} />;
};

export default BarRace;


