import { useEffect, useState } from "react";
import * as d3 from "d3";
import data from "../assets/barChart1.json";
import DateSlider2 from "./DateSlider2";

function BarPlot(){//{ selectedGroups }) {
  if (!data) return <p>Loading viz...</p>;

  return <GraphVisualization data={data}/>;// selectedGroups={selectedGroups} />;
}

function GraphVisualization({ data}){//, selectedGroups }) {
  //const svgRef = useRef();
  const [dateRange, setDateRange] = useState([0, 2023-2010+1]);
  useEffect(() => {
    d3.select("#bar-container").select("svg").remove();

    const width = 900;
    const height = data.length * 30;
    const color = d3.scaleOrdinal(d3.schemeCategory10);

    const filteredData = data;//data.filter(d => d.group.some(group => selectedGroups.has(group)));
    
    //let filteredNodes = nodes.filter(node => node.genres.some(genre => selectedGroups.has(genre))
    //);
    const xScale = d3.scaleLinear()
      .domain([0, d3.max(filteredData, d => d3.sum(d.positive.slice(0, 10)))])//d3.mean(d.positive.slice(dateRange[0], dateRange[1])))])
      .range([0, width]);

    const yScale = d3.scaleBand()
      .domain(filteredData.map(d => d.socialmedia))
      .range([0, height])
      .padding(0.2);

    const svg = d3.select("#bar-container")
      .append("svg")
      .attr("width", width)
      .attr("height", height)
      .attr("viewBox", [-width / 2, -height / 2, width, height])
      .style("max-width", "100%")
      .style("height", "auto")
      .style("border", "1px solid #333533")
      .style("border-radius", "8px");

    const g = svg.append("g");

    
    const zoom = d3.zoom()
        .scaleExtent([0.5, 5]) // Facteur de zoom min/max
        .on("zoom", (event) => {
        g.attr("transform", event.transform); // Applique la transformation sur le groupe contenant le graph
        });

        svg.call(zoom); // Active le zoom sur le SVG

    g.selectAll("rect")
      .data(filteredData, d => d.socialmedia)
      .join(
        enter => enter.append("rect")
          .attr("x", 0)
          .attr("y", d => yScale(d.socialmedia))
          .attr("height", yScale.bandwidth())
          .attr("width", 0) 
          .attr("fill", d => color(d.socialmedia))
          .transition()
          .duration(500)
          .attr("width", d =>xScale(d3.sum(d.positive.slice(dateRange[0], dateRange[1])))),
        update => update
          .transition()
          .duration(500)
          .attr("y", d => yScale(d.socialmedia))
          .attr("width", d => xScale(d3.sum(d.positive.slice(dateRange[0], dateRange[1])))),
        exit => exit
          .transition()
          .duration(300)
          .attr("width", 0)
          .remove()
      );

    g.selectAll("text")
      .data(filteredData, d => d.socialmedia)
      .join(
        enter => enter.append("text")
          .attr("x", 5)
          .attr("y", d => yScale(d.socialmedia) + yScale.bandwidth() / 2)
          .attr("dy", "0.35em")
          .text(d => `${d.socialmedia}: ${d.socialmedia}`)
          .style("fill", "white"),
        update => update
          .transition()
          .duration(500)
          .attr("y", d => yScale(d.socialmedia) + yScale.bandwidth() / 2)
          .text(d => `${d.socialmedia}: ${d.positive[5]}`),//d3.mean(d.nb_game.slice(dateRange[0], dateRange[1])) }`),
        exit => exit
          .transition()
          .duration(300)
          .style("opacity", 0)
          .remove()
      );
  }, [data, dateRange]);

  return (
    <div id="bar-container">
    <div className="slider-container">
        <DateSlider2 minDate={0} maxDate={2023-2010+1} dateRange={dateRange} setDateRange={setDateRange} className="date-slider2" label="Date Range2"/>
    </div>
  </div>);
}

export default BarPlot;
