import React, { useEffect, useRef, useState } from "react";
//import data from "../assets/category-brands.csv";
import * as d3 from "d3";
import data from "../assets/barRace1.json";

const generateKeyframes = () => {
  const data = [
    { name: "A", value: Math.random() * 100 },
    { name: "B", value: Math.random() * 100 },
    { name: "C", value: Math.random() * 100 },
    { name: "D", value: Math.random() * 100 },
  ];

  // Générer des keyframes avec classement dynamique
  const keyframes = Array.from({ length: 20 }, (_, i) => {
    // Augmenter les valeurs de manière aléatoire
    const updatedData = data.map((d) => ({ ...d, value: d.value + Math.random() * 10 }));

    // Classer les données par valeur décroissante et leur attribuer un rang
    const rankedData = updatedData
      .sort((a, b) => b.value - a.value)
      .map((d, index) => ({ ...d, rank: index }));

    return [`2025-03-${i + 1}`, rankedData]; // Keyframe : date + données classées
  });

  return keyframes;
};

  
  const BarRace = ({duration = 250, width = 800, height = 600, n = 12 }) => {
    const keyframes = data;//generateKeyframes();
    //const keyframes = generateKeyframes();
    console.log(keyframes);
    const svgRef = useRef();
    const [replay, setReplay] = useState(false); // État pour gérer le replay
    const barSize = 48;
    const marginTop = 16;
    const marginBottom = 6;
    const marginLeft = 0;
    const marginRight = 6;

    console.log("n : ", n);
  
    const x = d3.scaleLinear([0, 1], [marginLeft, width - marginRight]);
    const y = d3
      .scaleBand()
      .domain(d3.range(n))
      .rangeRound([marginTop, marginTop + barSize * n])
      .padding(0.1);
  
    const colorScale = d3.scaleOrdinal(d3.schemeTableau10);
  
    const replayAnimation = () => {
      setReplay(false);
      setTimeout(() => setReplay(true), 0); // Redémarrer l'animation en modifiant l'état
    };
  
    useEffect(() => {
      const svg = d3.select(svgRef.current);
      svg.selectAll("*").remove(); // Réinitialiser SVG à chaque redémarrage
      svg
        .attr("viewBox", [0, 0, width, height])
        .attr("width", width)
        .attr("height", height);
  
      const gBars = svg.append("g").attr("fill-opacity", 1);
      const gLabels = svg.append("g").attr("text-anchor", "end");
      const gTicker = svg.append("text").attr("x", width - 6).attr("y", marginTop).style("font", `bold ${barSize}px sans-serif`);
  
      const updateBars = (data, transition) => {
        const bars = gBars.selectAll("rect").data(data, (d) => d.name);
  
        bars
          .join(
            (enter) =>
              enter
                .append("rect")
                .attr("fill", (d) => colorScale(d.name))
                .attr("x", x(0))
                .attr("y", (d) => y(d.rank))
                .attr("height", y.bandwidth())
                .attr("width", (d) => x(d.value) - x(0)),
            (update) => update,
            (exit) => exit.transition(transition).attr("width", 0).remove()
          )
          .call((bar) =>
            bar
              .transition(transition)
              .attr("y", (d) => y(d.rank)) // Mise à jour correcte de la position verticale
              .attr("width", (d) => x(d.value) - x(0))
          );
      };
  
      const updateLabels = (data, transition) => {
        const labels = gLabels.selectAll("text").data(data, (d) => d.name);
  
        labels
          .join(
            (enter) =>
              enter
                .append("text")
                .attr("x", -6)
                .attr("y", (d) => y(d.rank) + y.bandwidth() / 2)
                .attr("dy", "0.35em")
                .text((d) => d.name),
            (update) => update,
            (exit) => exit.transition(transition).attr("opacity", 0).remove()
          )
          .call((label) =>
            label
              .transition(transition)
              .attr("y", (d) => y(d.rank) + y.bandwidth() / 2) // Mise à jour correcte de l'emplacement
          );
      };
  
      const updateTicker = (date, transition) => {
        gTicker.transition(transition).text(date);
      };

      (async function animateChart() {
        console.log("keyframes : ", keyframes);
        for (let i = 0; i< length(keyframes);i++) {
          console.log("i : ", i);
          //const transition = svg.transition().duration(duration).ease(d3.easeLinear);
          //const date = elem.date;
          //const data = elem.val;
          //console.log("elem : ", elem);
          /*
          x.domain([0, d3.max(data, (d) => d.value)]);
  
          updateBars(data, transition);
          updateLabels(data, transition);
          updateTicker(date, transition);
  
          await transition.end();
          */
        }
      })();
      
    }, [keyframes, duration, replay, x, y, colorScale]); // Rejouer l'animation lorsqu'on change l'état replay
  
    return (
      <div>
        <button onClick={replayAnimation}>Replay</button> {/* Bouton Replay */}
        <svg ref={svgRef}></svg>
      </div>
    );
  };
  
  export default BarRace;


