import * as d3 from "d3";
import { useState, useEffect } from "react";

function Histogram() {
  const [data, setData] = useState(null);

  useEffect(() => {
    d3.csv("../assets/alaphabet.csv", d3.autoType).then((loadedData) => {
      setData(loadedData);
    });
  }, []);

  if (!data) return <p>Loading viz...</p>;

  return <GraphVisualization data={data} />;
}

function GraphVisualization({ data }) {
  const width = 928;
  const height = 500;
  const marginTop = 20;
  const marginRight = 0;
  const marginBottom = 30;
  const marginLeft = 40;

  useEffect(() => {
    d3.select("#hist-container").select("svg").remove();

    const x = d3
      .scaleBand()
      .domain(d3.sort(data, (d) => -d.frequency).map((d) => d.letter))
      .range([marginLeft, width - marginRight])
      .padding(0.1);

    const xAxis = d3.axisBottom(x).tickSizeOuter(0);

    const y = d3
      .scaleLinear()
      .domain([0, d3.max(data, (d) => d.frequency)])
      .nice()
      .range([height - marginBottom, marginTop]);

    // Créer le conteneur SVG
    const svg = d3
      .select("#hist-container")
      .append("svg")
      .attr("viewBox", [0, 0, width, height])
      .attr("width", width)
      .attr("height", height)
      .attr("style", "max-width: 100%; height: auto;");

    // Ajouter les barres
    svg
      .append("g")
      .attr("fill", "steelblue")
      .selectAll("rect")
      .data(data)
      .join("rect")
      .attr("x", (d) => x(d.letter))
      .attr("y", (d) => y(d.frequency))
      .attr("height", (d) => y(0) - y(d.frequency))
      .attr("width", x.bandwidth());

    svg
      .append("g")
      .attr("transform", `translate(0,${height - marginBottom})`)
      .call(xAxis);

    svg
      .append("g")
      .attr("transform", `translate(${marginLeft},0)`)
      .call(d3.axisLeft(y))
      .call((g) => g.select(".domain").remove());
  }, [data]);

  return <div id="hist-container"></div>;
}

export default Histogram;
