import { useEffect, useState, useRef } from "react";
import * as d3 from "d3";
import data from "../assets/flare.json";


function TreeMap(){//{ selectedGroups }) {
    if (!data) return <p>Loading viz...</p>;
  
    return <GraphVisualization/>; //data={data} selectedGroups={selectedGroups} />;
  }

const GraphVisualization = () => {
    const svgRef = useRef(null);
    const width = 1154;
    const height = 1154;

    useEffect(() => {
        if (!data) return;

        // Clear previous SVG content
        //d3.select(svgRef.current).selectAll("*").remove();
        d3.select("#treemap-container").select("svg").remove();
        // Define color scale
        const color = d3.scaleOrdinal(data.children.map((d) => d.name), d3.schemeTableau10);

        // Compute the treemap layout
        const root = d3.treemap()
        .tile(d3.treemapSquarify)
        .size([width, height])
        .padding(1)
        .round(true)(
            d3.hierarchy(data)
            .sum((d) => d.value)
            .sort((a, b) => b.value - a.value)
        );

        // Create SVG and group element for each leaf
        const svg = d3.select("#treemap-container")
        .append("svg")
        .attr("viewBox", `0 0 ${width} ${height}`)
        .style("font", "10px sans-serif");


        const g = svg.append("g");
        const zoom = d3.zoom()
            .scaleExtent([0.5, 5]) // Facteur de zoom min/max
            .on("zoom", (event) => {
            g.attr("transform", event.transform); // Applique la transformation sur le groupe contenant le graph
            });
    
            svg.call(zoom); // Active le zoom sur le SVG

        const leaf = svg.selectAll("l")
        .data(root.leaves())
        .join("l")
        .attr("transform", (d) => `translate(${d.x0},${d.y0})`);

        // Append tooltip
        const format = d3.format(",d");
        leaf.append("title").text((d) =>
        `${d.ancestors().reverse().map((d) => d.data.name).join(".")}\n${format(d.value)}`
        );

        // Append colored rectangles
        leaf.append("rect")
        .attr("fill", (d) => {
            while (d.depth > 1) d = d.parent;
            return color(d.data.name);
        })
        .attr("fill-opacity", 0.6)
        .attr("width", (d) => d.x1 - d.x0)
        .attr("height", (d) => d.y1 - d.y0);

        // Append text inside each rectangle
        leaf.append("text")
        .selectAll("tspan")
        .data((d) => d.data.name.split(/(?=[A-Z][a-z])|\s+/g).concat(format(d.value)))
        .join("tspan")
        .attr("x", 3)
        .attr("y", (d, i, nodes) => `${(i === nodes.length - 1) * 0.3 + 1.1 + i * 0.9}em`)
        .attr("fill-opacity", (d, i, nodes) => (i === nodes.length - 1 ? 0.7 : null))
        .text((d) => d);

    }, [data]);
    return <div id="treemap-container"></div>;
    };

export default TreeMap;
