import { useEffect, useState } from "react";
import * as d3 from "d3";
import data from "../assets/alphabet.csv";
import DateSlider2 from "./DateSlider2";
import NewSlider from "./NewSlider";

function Histogram(){//{ selectedGroups }) {
  if (!data) return <p>Loading viz...</p>;

  return <GraphVisualization/>;
}

function GraphVisualization(){
    // Specify the chart’s dimensions.
    const width = 928;
    const height = 500;
    const marginTop = 20;
    const marginRight = 0;
    const marginBottom = 30;
    const marginLeft = 40;
  
    // Create the horizontal scale and its axis generator.
    const x = d3.scaleBand()
      .domain(d3.sort(data, d => -d.frequency).map(d => d.letter))
      .range([marginLeft, width - marginRight])
      .padding(0.1);
  
    const xAxis = d3.axisBottom(x).tickSizeOuter(0);
  
  
    // Create the vertical scale.
    const y = d3.scaleLinear()
      .domain([0, d3.max(data, d => d.frequency)]).nice()
      .range([height - marginBottom, marginTop]);
  
    // Create the SVG container and call the zoom behavior.
    const svg = d3.create("svg")
        .attr("viewBox", [0, 0, width, height])
         .attr("width", width)
        .attr("height", height)
        .attr("style", "max-width: 100%; height: auto;")
        .call(zoom);
  
    // Append the bars.
    svg.append("g")
        .attr("class", "bars")
        .attr("fill", "steelblue")
      .selectAll("rect")
      .data(data)
      .join("rect")
        .attr("x", d => x(d.letter))
        .attr("y", d => y(d.frequency))
        .attr("height", d => y(0) - y(d.frequency))
        .attr("width", x.bandwidth());
  
    // Append the axes.
    svg.append("g")
        .attr("class", "x-axis")
        .attr("transform", `translate(0,${height - marginBottom})`)
        .call(xAxis);
  
    svg.append("g")
        .attr("class", "y-axis")
        .attr("transform", `translate(${marginLeft},0)`)
        .call(d3.axisLeft(y))
        .call(g => g.select(".domain").remove());
  
    return svg.node();
  
    function zoom(svg) {
      const extent = [[marginLeft, marginTop], [width - marginRight, height - marginTop]];
  
      svg.call(d3.zoom()
          .scaleExtent([1, 8])
          .translateExtent(extent)
          .extent(extent)
          .on("zoom", zoomed));
  
      function zoomed(event) {
        x.range([marginLeft, width - marginRight].map(d => event.transform.applyX(d)));
        svg.selectAll(".bars rect").attr("x", d => x(d.letter)).attr("width", x.bandwidth());
        svg.selectAll(".x-axis").call(xAxis);
      }
    }
  }

export default Histogram;
